package com.zhiwang.zfm.common.util;

import java.text.ParseException;

/**
 * 站内信 枚举类
 * 
 * @author huangzhenggang
 * @date 2018年5月23日
 */
public enum MsgContentEnum {
	//申请资料提交成功
	M0001(1),
	// 审核未通过
	M0002(2),
	// 审核通过
	M0003(3),
	// 放款成功
	M0004(4),
	// 授权失败
	M0005(5),
	// 展期成功
	M0006(6),
	// 展期失败
	M0007(7),
	// 还款提醒
	M0008(8),
	// 还款成功
	M0009(9),
	// 还款失败
	M0010(10),
	// 奖金提现成功
	M0011(11),
	// 奖金提现失败
	M0012(12);
	private Integer type;

	MsgContentEnum(Integer type) {
		this.type = type;
	}

	/**
	 * 
	 * @param currentTime 当前时间 (用于展期/还款/申请提现 发起时间)
	 * @param orderTime 订单创建时间
	 * @param repaymentTime  还款时间
	 * @param repaymentAmount 还款金额
	 * @return
	 * @throws ParseException
	 */
	public String[] getMsg(String currentTime,String orderTime,String repaymentTime,String repaymentAmount) throws ParseException {
		Integer type = this.type;
		String[] message = null;
		// 消息标题
		String title = "";
		// 消息内容
		String text = "";
		//申请发起时间(yyyy年mm月dd日hh点mm分)
		currentTime = ChkUtil.isEmpty(currentTime)?"":DateUtils.formatDate(currentTime,DateUtils.DATE_TIME_PATTERN,DateUtils.STYLE_7);
		//订单创建时间 (yyyy年mm月dd日hh点mm分)
		orderTime = ChkUtil.isEmpty(orderTime)?"":DateUtils.formatDate(orderTime,DateUtils.DATE_TIME_PATTERN,DateUtils.STYLE_7);
		//还款时间(yyyy年mm月dd日)
		repaymentTime = ChkUtil.isEmpty(repaymentTime                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             )?"":DateUtils.formatDate(repaymentTime,DateUtils.DATE_TIME_PATTERN,DateUtils.STYLE_8);

		switch (type) {
		case 1:
			// 提交成功通知
			title = "提交成功通知";
			text = "尊敬的客户您好，您于" + orderTime + "发起的借款申请已经完成了资料提交，我们将尽快处理您的借款申请，请耐心等待。";
			break;
		case 2:
			// 借款申请未通过通知
			title = "借款申请未通过通知";
			text = "抱歉，您于" + orderTime + "发起的借款申请未能通过。";
			break;
		case 3:
			// 提现通知
			title = "提现通知";
			text = "尊敬的客户您好，您于" + orderTime + "发起的借款申请已审核通过，您可以立即申请提现";
			break;
		case 4:
			// 放款通知
			title = "放款通知";
			text = "尊敬的客户您好，您于" + orderTime + "发起的借款申请已发放至您的银行卡内，请及时查看。";
			break;
		case 5:
			// 授权通知
			title = "授权通知";
			text = "尊敬的客户您好，您于" + orderTime + "发起的借款申请有部分授权认证失败，请您进入授权管理重新认证。";
			break;
		case 6:
			// 展期成功
			title = "展期通知";
			text = "尊敬的客户您好，您于" + currentTime + "发起的展期申请已成功，展期后还款日为"+repaymentTime+"。";
			break;
		case 7:
			// 展期失败
			title = "展期通知";
			text = "尊敬的客户您好，您于" + currentTime + "发起的展期申请失败，请确保卡内余额充足。 ";
			break;
		case 8:
			// 还款通知
			title = "还款通知";
			text = "尊敬的客户您好，您本次应还款总额为：" + repaymentAmount + "元,请在"+repaymentTime+"之前进行还款，否则会对您的个人征信记录造成不利影响。";
			break;
		case 9:
			// 还款结清通知
			title = "还款结清通知";
			text = "尊敬的客户您好，您于" + orderTime + "申请的借款订单已结清，您可以再次申请借款，按时结清后有助于提升您的借款额度。";
			break;
		case 10:
			// 还款失败
			title = "还款通知";
			text = "尊敬的客户您好，您于" + currentTime + "发起的还款申请失败，请确保卡内余额充足。";
			break;
		case 11:
			// 奖金提现成功
			title = "提现通知";
			text = "尊敬的客户您好，您于" + currentTime + "发起的奖金提现金额已成功发放至您的绑定银行卡内，请及时查看。";
			break;
		case 12:
			// 奖金提现失败
			title = "提现通知";
			text = "尊敬的客户您好，您于" + currentTime + "发起的奖金提现申请失败，请您重新尝试。";
			break;
		
		default:
			break;
		}
		message = new String[] { title, text };
		return message;
	}
}
